import React, { useState, useEffect } from "react";
import { AiOutlineHeart, AiFillHeart } from "react-icons/ai";
import { TbRating18Plus } from "react-icons/tb";

function Card({ movie }) {
  const [isLiked, setIsLiked] = useState(false);

  // Charger les données de localStorage lors du montage du composant
  useEffect(() => {
    const likedMovies = JSON.parse(localStorage.getItem("likedMovies")) || [];
    setIsLiked(likedMovies.includes(movie.id));
  }, [movie.id]);

  const toggleLike = () => {
    const likedMovies = JSON.parse(localStorage.getItem("likedMovies")) || [];

    if (isLiked) {
      // Retirer le film de la liste des films likés
      const updatedLikedMovies = likedMovies.filter((id) => id !== movie.id);
      localStorage.setItem("likedMovies", JSON.stringify(updatedLikedMovies));
    } else {
      // Ajouter le film à la liste des films likés
      likedMovies.push(movie.id);
      localStorage.setItem("likedMovies", JSON.stringify(likedMovies));
    }

    setIsLiked(!isLiked);
  };

  return (
    <div>
      <div className="card">
        {movie.poster_path ? (
          <img
            src={"https://image.tmdb.org/t/p/w300/" + movie.poster_path}
            alt={"Affiche du film " + movie.title}
            height={450}
          />
        ) : (
          <img
            src="data:image/png;base64,"
            alt={"Affiche du film " + movie.title}
          />
        )}
        <p className="title">{movie.title}</p>
        <p>{movie.release_date}</p>
        <p>{movie.vote_average}</p>
        <p className="overview">{movie.overview.length > 150}</p>
        <span className="heartIconCard" onClick={toggleLike}>
          {isLiked ? (
            <AiFillHeart size={40} className="heartIconCard" />
          ) : (
            <AiOutlineHeart size={40} className="heartIconCard" />
          )}
        </span>
        <span className="RatingPlusIcon">
          {movie.adult && (
            <TbRating18Plus size={60} className="RatingPlusIcon" />
          )}
        </span>
      </div>
    </div>
  );
}

export default Card;
